var db = require('db');

//Объявление класса и его конструктора

function User(name) {
    //Параметр, указанный в скобках при создании нового объекта
    //этого класса будет являться переменной name
    this.name = name;
}

//Метод класса
User.prototype.hello = function(who) {
    console.log(db.getPhrase("Hell") + ", " + who.name);
};

module.exports = User;